    <script> 
 
 $(function() { 
             

            $('input[name="dashdatefilter"]').daterangepicker({
               // autoUpdateInput: false,      
                startDate: moment().subtract(89, 'days'),
                endDate: moment(),    
                ranges: {
                'Today': [moment(), moment()],
                'Yesterday': [moment().subtract(1, 'days'), moment().subtract(1, 'days')],
                'Last 7 Days': [moment().subtract(6, 'days'), moment()],
                'Last 30 Days': [moment().subtract(29, 'days'), moment()],
                'Last 90 Days': [moment().subtract(89, 'days'), moment()],
                'Last Year': [moment().subtract(1, 'year'), moment()],
                'This Month': [moment().startOf('month'), moment().endOf('month')],
                'Last Month': [moment().subtract(1, 'month').startOf('month'), moment().subtract(1, 'month').endOf('month')]
                } ,
                locale: {
                    format: 'DD/MM/YYYY'
                }                         
            }); 
            $('input[name="dashdatefilter"]').on('apply.daterangepicker', function(ev, picker) {
                $(this).val(picker.startDate.format('DD/MM/YYYY') + ' - ' + picker.endDate.format('DD/MM/YYYY'));
            }); 
            $('input[name="dashdatefilter"]').on('cancel.daterangepicker', function(ev, picker) {
                $(this).val('');
            });

                                      
            
 
            $('input[name="start-date"]').daterangepicker({
                singleDatePicker: true,
                showDropdowns: true,
                minYear: 2019,
                maxYear: parseInt(moment().format('YYYY'),10),
                locale: {
                    format: 'DD/MM/YYYY'
                }
            }, function(start, end, label) {
                
            });

            $('input[name="expiry-date"]').daterangepicker({
                singleDatePicker: true,
                showDropdowns: true,
                minYear: 2019,
                maxYear: parseInt(moment().format('YYYY'),10),
                locale: {
                    format: 'DD/MM/YYYY'
                }
            }, function(start, end, label) {  
            }); 
        });

    $.extend( true, $.fn.dataTable.defaults, {
    "searching": false,
    "ordering": false
    } );

    $(document).ready( function () { 
        var vh = window.innerHeight;
        var calcvh = vh - 390;  
        
        
          
        var thetable = $('#campaign').DataTable(  {
            dom: 'Bfrtip',
            buttons: [
                'copy', 'excel'
            ]
        } ); 

        var impresssions = thetable.column( 2 ).data().sum();
        var clicks = thetable.column( 3 ).data().sum();
        var spends = thetable.column( 5 ).data().sum();
        var cpm = thetable.column( 6 ).data().average();
        var cpc = thetable.column( 7 ).data().average();
        var ctr = thetable.column( 8 ).data().average(); 
        if ( ctr > 0) { } else { ctr = 0; }
        if ( cpm > 0) { } else { cpm = 0; } 
        document.getElementById("aggrimpressions").innerHTML = impresssions.toLocaleString();
        document.getElementById("aggrclicks").innerHTML = clicks.toLocaleString();
        document.getElementById("aggrspends").innerHTML = '$'+ spends.toLocaleString();
        document.getElementById("aggrcpm").innerHTML = '$'+ cpm.toFixed(3);
        document.getElementById("aggrcpc").innerHTML = '$'+ cpc.toFixed(3); 
        ctr = (clicks / impresssions)*100;
        document.getElementById("aggrctr").innerHTML = ctr.toFixed(3)+'%';


            


    }); // End document ready
    </script>
	
	
